import React, {Component} from 'react';
import './Game.css';
import { MDBBtn, MDBIcon } from "mdbreact";
import Webcam from 'react-webcam';
import axios from 'axios';

class Game extends Component {

    setRef = (webcam) => {
        this.webcam = webcam;
    }

    capture = () => {
        const imageSrc = this.webcam.getScreenshot();
        const username = "alexander"
        axios.post('/image/save', {username, imageSrc}).then(res => console.log(res))

    };

    getImage = () => {
        axios.get('/image/findImage').then(res => res)
    }

    render() {

        const videoConstraints = {
            width: 1280,
            height: 720,
            facingMode: 'user',
        }

        return (
            <div className="gamearea mx-auto mt-4 col-md-12">
            <img src={this.getImage} />
            <Webcam 
                audio={false}
                height={500}
                ref={this.setRef}
                screenshotFormat="image/jpeg"
                width={500}
                videoConstraints={videoConstraints}
            />
            <div className="icons mx-auto">
            <MDBBtn><MDBIcon icon="camera-retro" size="6x" onClick={this.capture}/></MDBBtn>
            </div>
        </div>
        ) 
    }
}

export default Game
