import React, {Component} from 'react';
import './Game.css';
import { MDBBtn, MDBIcon } from "mdbreact";
import Webcam from 'react-webcam';
import axios from 'axios';
import { Redirect } from 'react-router-dom';

class Game extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: 0,
            picture: "None",
            // imageSrc: false,
            celebrityName: ""
        }
    }

    setRef = (webcam) => {
        this.webcam = webcam;
    }

//when a user logs in save this below and when they take the picture, log this.

    capture = () => {
        const imageSrc = this.webcam.getScreenshot();
        const username = Math.floor(Math.random() * 100);
        axios.post('/image/save', {username, imageSrc}).then(res => res);
        setTimeout(() => {
            this.setState({username})
        }, 200)
        axios.post('/image/clarifai', {imageSrc}).then(res => this.setState({celebrityName: res.data.outputs[0].data.regions[0].data.face.identity.concepts[0].name}))

    };

    handleChange = (e) => {
        this.setState({
         [e.target.id]: e.target.value
        })
     }

     handleSwitch = () => {
         if (this.state.picture === "None") {
             this.setState({picture: "None", text: "There you are looking radiant!"});
         } else {
             this.setState({form: "Login", text: "Dont have an Account? Register now!"});
         }

     }


    render() {

        const videoConstraints = {
            width: 400,
            height: 400,
            facingMode: 'user',
        }

        return (
            <div className="gamearea mx-auto mt-4 col-md-12">
            <p>There you are looking radiant! <br />Smile for the camera!</p>
            <div className="icons">
            <MDBBtn><MDBIcon icon="camera-retro" size="5x" onClick={this.capture}/></MDBBtn></div><br/>
            <Webcam 
                audio={false}
                height={500}
                ref={this.setRef}
                screenshotFormat="image/jpeg"
                width={400}
                videoConstraints={videoConstraints}
            />
            <br/>
            <img src={`/image/${this.state.username}/findImage`} />

            <div>Your carefully calculated celebrity look-alike is: {this.state.celebrityName}</div>
            </div>
        ) 
    }
}

export default Game
