import React, {Component} from 'react';
import './Game.css';
import { MDBBtn, MDBIcon } from "mdbreact";
import Webcam from 'react-webcam';
import axios from 'axios';

import { Redirect } from 'react-router-dom';

// import Clarifai from 'clarifai';

class Game extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: 0,
            imageSrc: false
        }
    }

    setRef = (webcam) => {
        this.webcam = webcam;
    }

//when a user logs in save this below and when they take the picture, log this.

    capture = () => {
        const imageSrc = this.webcam.getScreenshot();
        const username = Math.floor(Math.random() * 100);
        axios.post('/image/save', {username, imageSrc}).then(res => res);
        setTimeout(() => {
            this.setState({username})
        }, 200)

    };

    // identifyImage(imageData){
    //     // Initialize Clarifai api

    //     const Clarifai = require('clarifai');

    //     const app = new Clarifai.App({
    //         apiKey: '8469266d2783471ead47f572d20b4171'
    //     });

    //     // Identify the image
    //     app.models.predict(Clarifai.FACE_DETECT_MODEL, {base64: imageData})
    //     .then((response) => this.displayAnswer(response.outputs[0].data.concepts[0].name)
    //     .catch((err) => alert(err))
    //     );
    // }


    render() {

        if (this.state.imageSrc === true) {
            return <Redirect to='/Match'/>
        }

        const videoConstraints = {
            width: 1280,
            height: 720,
            facingMode: 'user',
        }

        return (
            <div className="gamearea mx-auto mt-4 col-md-12">
            <img src={`/image/${this.state.username}/findImage`} />
            <Webcam 
                audio={false}
                height={500}
                ref={this.setRef}
                screenshotFormat="image/jpeg"
                width={500}
                videoConstraints={videoConstraints}
            />
            <div className="icons mx-auto">
            <MDBBtn><MDBIcon icon="camera-retro" size="6x" onClick={this.capture}/></MDBBtn>
            </div>
        </div>
        ) 
    }
}

export default Game
